import { useEffect, useState } from "react";
import map from "./artifacts/deployments/map.json";
import FaucetArtifact from "./artifacts/contracts/Faucet.json";
import { ethers } from "ethers";

export default function App() {
  const address = map[1337]["Faucet"][0];
  const faucetABI = FaucetArtifact.abi;
  const [web3Api, setWeb3Api] = useState({
    provider: null,
  });

  const [account, setAccount] = useState(null);

  function timeout(delay) {
    return new Promise((res) => setTimeout(res, delay));
  }

  useEffect(() => {
    const loadProvider = async () => {
      let provider;
      if (window.ethereum) {
        provider = new ethers.providers.Web3Provider(window.ethereum);
        // Prompt user for account connections
        await provider.send("eth_requestAccounts", []);
      } else if (window.web3) {
        provider = new ethers.providers.getDefaultProvider();
      } else if (!process.env.production) {
        provider = new ethers.providers.JsonRpcProvider(
          "http://localhost:7545"
        );
      }
      setWeb3Api({
        provider,
      });
    };

    loadProvider();
  }, []);

  useEffect(() => {
    const getAccounts = async () => {
      const accounts = await web3Api.provider.getSigner();
      setAccount(accounts[0]);
    };

    web3Api.provider && getAccounts();
    console.log(account);
  }, [web3Api.provider]);

  return (
    <>
      <div className="h-full flex justify-center items-center bg-slate-200">
        <div>
          <div>
            <span className="mr-2">Account:</span>
            <button
              onClick={async () => {
                const accounts = await window.ethereum.request({
                  method: "eth_requestAccounts",
                });
                console.log(accounts);
              }}
              className="connectButton"
            >
              Connect Wallet
            </button>
          </div>
          <div className="text-[36px]">
            Current Balance - <strong>10</strong> ETH
          </div>

          <button className="actionButton bg-green-200 hover:bg-green-500 text-green-500 ">
            Donate
          </button>
          <button className="actionButton bg-red-200 hover:bg-red-500  text-red-500 ">
            Withdraw
          </button>
        </div>
      </div>
      )
    </>
  );
}

