import { useEffect, useState } from "react";
import map from "./artifacts/deployments/map.json";
import FaucetArtifact from "./artifacts/contracts/Faucet.json";
import { ethers } from "ethers";

export default function App() {
  const address = map[1337]["Faucet"][0];
  const faucetABI = FaucetArtifact.abi;

  return (
    <>
      {window.ethereum ? (
        <div className="h-full flex justify-center items-center bg-slate-200">
          <div>
            <div>
              <span className="mr-2">Account:</span>
              {account ? (
                account
              ) : (
                <button
                  onClick={connectWalletHandler}
                  className="connectButton"
                >
                  Connect Wallet
                </button>
              )}
            </div>
            <div className="text-[36px]">
              Current Balance - <strong>{contractInfo.balance}</strong> ETH
            </div>

            <button
              onClick={donateEth}
              className="actionButton bg-green-200 hover:bg-green-500 text-green-500 "
            >
              Donate
            </button>
            <button
              onClick={withdrawEth}
              className="actionButton bg-red-200 hover:bg-red-500  text-red-500 "
            >
              Withdraw
            </button>
          </div>
        </div>
      ) : (
        <div className="flex items-center justify-center h-full">
          <a
            className="bg-gray-800 hover:bg-gray-700 border-b-4 border-black hover:border-gray-800 text-white text-center py-2 px-4 rounded"
            rel="noreferrer"
            href="https://metamask.io/download"
            target="_blank"
          >
            Install Metamask
          </a>
        </div>
      )}
    </>
  );
}
